import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  GitBranch, 
  Code, 
  Star, 
  Users, 
  TrendingUp, 
  Activity,
  Calendar,
  Github,
  ChevronRight,
  GitCommit,
  GitPullRequest,
  GitFork
} from 'lucide-react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '../components/ui/Card';
import { Button } from '../components/ui/Button';

// Animation variants
const container = {
  hidden: { opacity: 0 },
  show: {
    opacity: 1,
    transition: {
      staggerChildren: 0.1,
      delayChildren: 0.2,
    },
  },
};

const item = {
  hidden: { y: 20, opacity: 0 },
  show: { 
    y: 0, 
    opacity: 1,
    transition: {
      type: "spring",
      stiffness: 100,
      damping: 15
    }
  },
  hover: {
    y: -5,
    transition: { duration: 0.2 }
  },
  tap: { scale: 0.98 }
};

const Dashboard = () => {
  const [activeTab, setActiveTab] = useState('overview');
  
  // Mock GitHub profile data
  const githubStats = {
    username: 'john_doe',
    avatar: 'https://github.com/github.png',
    repositories: 42,
    followers: 125,
    following: 89,
    contributions: 847,
    languages: ['JavaScript', 'Python', 'TypeScript', 'Go', 'React'],
    topRepos: [
      { name: 'awesome-project', stars: 234, language: 'JavaScript', description: 'A collection of awesome things' },
      { name: 'ml-toolkit', stars: 187, language: 'Python', description: 'Machine learning utilities' },
      { name: 'react-components', stars: 156, language: 'TypeScript', description: 'Reusable React components' },
    ]
  };

  const recentActivity = [
    { 
      type: 'commit', 
      repo: 'awesome-project', 
      message: 'Added new feature for user authentication', 
      time: '2 hours ago',
      icon: <GitCommit className="h-4 w-4 text-blue-500" />
    },
    { 
      type: 'star', 
      repo: 'ml-toolkit', 
      message: 'Received a star from user_123', 
      time: '5 hours ago',
      icon: <Star className="h-4 w-4 text-yellow-500" />
    },
    { 
      type: 'fork', 
      repo: 'react-components', 
      message: 'Forked by developer_456', 
      time: '1 day ago',
      icon: <GitFork className="h-4 w-4 text-purple-500" />
    },
    { 
      type: 'pr', 
      repo: 'awesome-project', 
      message: 'Merged PR #42: Fixed payment processing bug', 
      time: '2 days ago',
      icon: <GitPullRequest className="h-4 w-4 text-green-500" />
    },
  ];

  const renderIcon = (type) => {
    switch(type) {
      case 'commit':
        return <GitCommit className="h-4 w-4 text-blue-500" />;
      case 'star':
        return <Star className="h-4 w-4 text-yellow-500" />;
      case 'fork':
        return <GitFork className="h-4 w-4 text-purple-500" />;
      case 'pr':
        return <GitPullRequest className="h-4 w-4 text-green-500" />;
      default:
        return <Activity className="h-4 w-4 text-gray-500" />;
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-50 to-gray-100 p-4 md:p-6">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
        className="max-w-7xl mx-auto"
      >
        {/* Header */}
        <motion.div 
          className="mb-8"
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.1 }}
        >
          <h1 className="text-4xl font-bold bg-clip-text text-transparent bg-gradient-to-r from-indigo-600 to-purple-600 mb-2">
            Dashboard
          </h1>
          <p className="text-gray-600">Welcome back! Here's your GitHub profile overview.</p>
          
          {/* Tabs */}
          <div className="flex space-x-1 mt-6 p-1 bg-gray-100 rounded-lg max-w-md">
            {['overview', 'repositories', 'projects', 'packages'].map((tab) => (
              <button
                key={tab}
                onClick={() => setActiveTab(tab)}
                className={`px-4 py-2 text-sm font-medium rounded-md transition-all duration-200 ${
                  activeTab === tab 
                    ? 'bg-white shadow text-indigo-600' 
                    : 'text-gray-600 hover:text-gray-900'
                }`}
              >
                {tab.charAt(0).toUpperCase() + tab.slice(1)}
              </button>
            ))}
          </div>
        </motion.div>

        <motion.div
          variants={container}
          initial="hidden"
          animate="show"
          className="space-y-6"
        >
          {/* GitHub Profile Section */}
          <motion.div variants={item}>
            <Card className="bg-white/80 backdrop-blur-md border border-gray-200/50 shadow-sm hover:shadow-md transition-shadow duration-300">
              <CardHeader>
                <div className="flex flex-col md:flex-row md:items-center space-y-4 md:space-y-0 md:space-x-6">
                  <div className="relative">
                    <div className="absolute -inset-1 bg-gradient-to-r from-purple-500 to-pink-500 rounded-full blur opacity-20 group-hover:opacity-30 transition duration-300"></div>
                    <div className="relative w-20 h-20 bg-gradient-to-r from-purple-500 to-pink-500 rounded-full flex items-center justify-center">
                      <Github className="w-10 h-10 text-white" />
                    </div>
                  </div>
                  <div className="flex-1">
                    <CardTitle className="text-2xl font-bold text-gray-900">@{githubStats.username}</CardTitle>
                    <CardDescription className="text-gray-600">Full Stack Developer & Open Source Contributor</CardDescription>
                    <div className="flex flex-wrap gap-2 mt-2">
                      <span className="px-2.5 py-0.5 text-xs font-medium bg-indigo-100 text-indigo-800 rounded-full">JavaScript</span>
                      <span className="px-2.5 py-0.5 text-xs font-medium bg-blue-100 text-blue-800 rounded-full">React</span>
                      <span className="px-2.5 py-0.5 text-xs font-medium bg-green-100 text-green-800 rounded-full">Node.js</span>
                    </div>
                  </div>
                  <div className="flex space-x-3">
                    <Button 
                      variant="outline" 
                      className="group transition-all duration-200 hover:bg-indigo-50 hover:border-indigo-200"
                      whileHover={{ scale: 1.02 }}
                      whileTap={{ scale: 0.98 }}
                    >
                      <Github className="mr-2 h-4 w-4" />
                      View Profile
                      <ChevronRight className="ml-1 h-4 w-4 opacity-0 group-hover:opacity-100 group-hover:translate-x-1 transition-all duration-200" />
                    </Button>
                  </div>
                </div>
              </CardHeader>
            </Card>
          </motion.div>

          {/* Stats Grid */}
          <motion.div variants={item}>
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4">
              {[
                { title: 'Repositories', value: githubStats.repositories, icon: <GitBranch className="h-5 w-5" />, change: '+3', color: 'indigo' },
                { title: 'Followers', value: githubStats.followers, icon: <Users className="h-5 w-5" />, change: '+12', color: 'blue' },
                { title: 'Contributions', value: githubStats.contributions, icon: <Activity className="h-5 w-5" />, change: 'This year', color: 'green' },
                { title: 'Following', value: githubStats.following, icon: <TrendingUp className="h-5 w-5" />, change: '+5', color: 'purple' },
              ].map((stat, index) => (
                <motion.div 
                  key={stat.title}
                  variants={item}
                  whileHover={item.hover}
                  whileTap={item.tap}
                >
                  <Card className="h-full bg-white/80 backdrop-blur-sm border border-gray-200/50 hover:border-gray-300/50 transition-all duration-300 hover:shadow-md">
                    <CardContent className="p-5">
                      <div className="flex justify-between items-start">
                        <div className="flex items-center justify-center w-12 h-12 rounded-lg bg-indigo-50 text-indigo-600 mb-4">
                          {React.cloneElement(stat.icon, { className: `h-5 w-5 text-${stat.color}-500` })}
                        </div>
                        <span className="text-xs px-2 py-1 bg-gray-100 text-gray-500 rounded-full">
                          {stat.change}
                        </span>
                      </div>
                      <h3 className="text-2xl font-bold text-gray-900">{stat.value}</h3>
                      <p className="text-sm text-gray-500">{stat.title}</p>
                    </CardContent>
                  </Card>
                </motion.div>
              ))}
            </div>
          </motion.div>

          {/* Main Content Grid */}
          <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
            {/* Languages Card */}
            <motion.div 
              variants={item}
              className="lg:col-span-1"
            >
              <Card className="h-full bg-white/80 backdrop-blur-sm border border-gray-200/50 hover:border-gray-300/50 transition-all duration-300 hover:shadow-md">
                <CardHeader>
                  <CardTitle className="flex items-center text-gray-800">
                    <Code className="mr-2 h-5 w-5 text-indigo-500" />
                    Top Languages
                  </CardTitle>
                  <CardDescription className="text-gray-500">Your most used languages</CardDescription>
                </CardHeader>
                <CardContent>
                  <div className="space-y-4">
                    {githubStats.languages.map((language, index) => {
                      const width = 90 - (index * 15);
                      const colorHue = index * 70 + 200;
                      return (
                        <div key={language} className="space-y-1.5">
                          <div className="flex items-center justify-between">
                            <span className="text-sm font-medium text-gray-800">{language}</span>
                            <span className="text-xs text-gray-500">{width}%</span>
                          </div>
                          <div className="w-full bg-gray-100 rounded-full h-2">
                            <motion.div 
                              className="h-full rounded-full"
                              initial={{ width: 0 }}
                              animate={{ width: `${width}%` }}
                              transition={{ duration: 0.8, delay: index * 0.1 }}
                              style={{ 
                                backgroundColor: `hsl(${colorHue}, 80%, 60%)`,
                                boxShadow: `0 0 10px hsla(${colorHue}, 80%, 60%, 0.4)`
                              }}
                            />
                          </div>
                        </div>
                      );
                    })}
                  </div>
                </CardContent>
              </Card>
            </motion.div>

            {/* Repositories & Activity */}
            <motion.div 
              variants={item}
              className="lg:col-span-2 space-y-6"
            >
              {/* Top Repositories */}
              <motion.div variants={item}>
                <Card className="bg-white/80 backdrop-blur-sm border border-gray-200/50 hover:border-gray-300/50 transition-all duration-300 hover:shadow-md">
                  <CardHeader>
                    <CardTitle className="flex items-center text-gray-800">
                      <Star className="mr-2 h-5 w-5 text-yellow-500" />
                      Top Repositories
                    </CardTitle>
                    <CardDescription className="text-gray-500">Your most starred repositories</CardDescription>
                  </CardHeader>
                  <CardContent>
                    <div className="space-y-3">
                      <AnimatePresence>
                        {githubStats.topRepos.map((repo, index) => (
                          <motion.div
                            key={repo.name}
                            initial={{ opacity: 0, y: 10 }}
                            animate={{ opacity: 1, y: 0 }}
                            transition={{ delay: index * 0.1 }}
                            whileHover={{ x: 5 }}
                            className="group"
                          >
                            <div className="flex flex-col p-4 rounded-lg bg-gray-50/50 hover:bg-gray-100/50 border border-gray-200/50 transition-all duration-200 cursor-pointer">
                              <div className="flex items-center justify-between">
                                <div className="flex items-center space-x-2">
                                  <Code className="h-4 w-4 text-gray-500" />
                                  <span className="font-medium text-gray-900 group-hover:text-indigo-600 transition-colors">
                                    {repo.name}
                                  </span>
                                </div>
                                <div className="flex items-center space-x-1 text-yellow-500">
                                  <Star className="h-4 w-4" />
                                  <span className="text-sm text-gray-600">{repo.stars}</span>
                                </div>
                              </div>
                              {repo.description && (
                                <p className="mt-1 text-sm text-gray-600 line-clamp-2">
                                  {repo.description}
                                </p>
                              )}
                              <div className="mt-2 flex items-center justify-between">
                                <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-indigo-100 text-indigo-800">
                                  {repo.language}
                                </span>
                                <ChevronRight className="h-4 w-4 text-gray-400 group-hover:text-indigo-500 transition-colors" />
                              </div>
                            </div>
                          </motion.div>
                        ))}
                      </AnimatePresence>
                    </div>
                  </CardContent>
                </Card>
              </motion.div>

              {/* Recent Activity */}
              <motion.div variants={item}>
                <Card className="bg-white/80 backdrop-blur-sm border border-gray-200/50 hover:border-gray-300/50 transition-all duration-300 hover:shadow-md">
                  <CardHeader>
                    <CardTitle className="flex items-center text-gray-800">
                      <Activity className="mr-2 h-5 w-5 text-purple-500" />
                      Recent Activity
                    </CardTitle>
                    <CardDescription className="text-gray-500">Your latest GitHub activity</CardDescription>
                  </CardHeader>
                  <CardContent>
                    <div className="space-y-4">
                      <AnimatePresence>
                        {recentActivity.map((activity, index) => (
                          <motion.div
                            key={`${activity.repo}-${index}`}
                            initial={{ opacity: 0, x: -10 }}
                            animate={{ opacity: 1, x: 0 }}
                            transition={{ delay: index * 0.05 }}
                            className="group"
                          >
                            <div className="relative pl-6 pb-4 border-l-2 border-gray-200 last:border-0 last:pb-0 group-hover:border-indigo-200 transition-colors">
                              <div className="absolute left-0 w-3 h-3 rounded-full bg-indigo-500 -translate-x-[calc(0.5rem+1px)] translate-y-1.5 group-hover:bg-indigo-600 transition-colors"></div>
                              <div className="flex items-start space-x-3">
                                <div className="flex-shrink-0 mt-0.5">
                                  {activity.icon}
                                </div>
                                <div className="flex-1 min-w-0">
                                  <p className="text-sm text-gray-800">
                                    <span className="font-medium text-gray-900 group-hover:text-indigo-600 transition-colors">
                                      {activity.repo}
                                    </span>{' '}
                                    {activity.message}
                                  </p>
                                  <p className="mt-1 text-xs text-gray-500">{activity.time}</p>
                                </div>
                              </div>
                            </div>
                          </motion.div>
                        ))}
                      </AnimatePresence>
                    </div>
                  </CardContent>
                </Card>
              </motion.div>
            </motion.div>
          </div>
        </motion.div>
      </motion.div>
    </div>
  );
};

export default Dashboard;
