import React, { useMemo } from 'react';
import { useNavigate } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';
import { Github, MessageCircle, Repeat2, Heart, Share2, Image as ImageIcon, Hash, Plus } from 'lucide-react';
import { Button } from '../components/ui/Button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '../components/ui/Card';

const HomeFeed = () => {
  const navigate = useNavigate();

  const posts = useMemo(() => ([
    {
      id: 1,
      author: { name: 'Sarah Chen', handle: '@sarah_chen', avatar: 'SC' },
      time: '2h',
      content: 'Building an AI-powered task manager that prioritizes your day automatically based on impact and deadlines. Looking for a business co-founder experienced in B2B SaaS.',
      tags: ['AI', 'Productivity', 'B2B', 'SaaS'],
      stats: { comments: 12, reposts: 8, likes: 64, shares: 3 }
    },
    {
      id: 2,
      author: { name: 'Marcus Johnson', handle: '@marcus_markets', avatar: 'MJ' },
      time: '5h',
      content: 'Exploring a FinTech idea: micro-investments for Gen Z with round-up spare change and AI-driven portfolios. Need a technical co-founder with Node + React.',
      tags: ['FinTech', 'Investing', 'React', 'Node'],
      stats: { comments: 9, reposts: 5, likes: 41, shares: 2 }
    },
    {
      id: 3,
      author: { name: 'Elena Rodriguez', handle: '@elena_ai', avatar: 'ER' },
      time: '1d',
      content: 'Healthcare NLP startup â€” extracting insights from clinical notes to reduce admin time by 40%. Looking for a business co-founder with healthcare ops experience.',
      tags: ['Healthcare', 'NLP', 'Startup'],
      stats: { comments: 22, reposts: 11, likes: 120, shares: 7 }
    }
  ]), []);

  return (
    <div className="relative min-h-screen bg-background overflow-hidden">
      {/* Ambient animated background */}
      <motion.div
        aria-hidden
        className="pointer-events-none absolute inset-0 -z-10"
        initial={{ opacity: 0 }}
        animate={{ opacity: 0.15 }}
        transition={{ duration: 1 }}
      >
        <div className="absolute -top-24 -left-24 w-80 h-80 rounded-full bg-primary blur-3xl opacity-30" />
        <div className="absolute -bottom-24 -right-24 w-96 h-96 rounded-full bg-accent blur-3xl opacity-30" />
      </motion.div>

      <div className="max-w-3xl mx-auto px-4 sm:px-6 lg:px-8 py-6 space-y-6">
        {/* Header */}
        <div className="sticky top-0 z-10 backdrop-blur supports-[backdrop-filter]:bg-background/60 bg-background/60 border-b border-border -mx-4 sm:-mx-6 px-4 sm:px-6 py-3">
          <h1 className="text-xl font-bold text-foreground">Home</h1>
        </div>

        {/* Composer */}
        <Card className="bg-card/80 backdrop-blur-sm border border-border/50">
          <CardContent className="pt-4">
            <div className="flex items-start gap-3">
              <div className="w-10 h-10 rounded-full bg-gradient-to-r from-purple-500 to-pink-500 text-white font-semibold flex items-center justify-center">YO</div>
              <div className="flex-1">
                <div className="w-full rounded-lg bg-muted px-4 py-3 text-muted-foreground cursor-text" onClick={() => navigate('/post-idea')}>
                  Share what you're building...
                </div>
                <div className="mt-3 flex items-center justify-between">
                  <div className="flex items-center gap-2 text-muted-foreground">
                    <Button type="button" variant="ghost" size="sm" className="px-2">
                      <ImageIcon className="h-4 w-4" />
                    </Button>
                    <Button type="button" variant="ghost" size="sm" className="px-2">
                      <Hash className="h-4 w-4" />
                    </Button>
                  </div>
                  <Button size="sm" onClick={() => navigate('/post-idea')}>
                    <Plus className="h-4 w-4 mr-2" />
                    Create Post
                  </Button>
                </div>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Feed */}
        <div className="space-y-4">
          <AnimatePresence>
            {posts.map((post, idx) => (
              <motion.div
                key={post.id}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -20 }}
                transition={{ delay: idx * 0.05 }}
              >
                <Card className="bg-card/80 backdrop-blur-sm border border-border/50">
                  <CardContent className="pt-5">
                    {/* Header */}
                    <div className="flex items-start gap-3">
                      <div className="w-10 h-10 rounded-full bg-gradient-to-r from-indigo-500 to-purple-500 text-white font-semibold flex items-center justify-center">
                        {post.author.avatar}
                      </div>
                      <div className="flex-1 min-w-0">
                        <div className="flex items-center gap-2">
                          <p className="font-medium text-foreground truncate">{post.author.name}</p>
                          <span className="text-muted-foreground truncate">{post.author.handle} Â· {post.time}</span>
                        </div>
                        {/* Content */}
                        <p className="mt-1 text-foreground whitespace-pre-line">{post.content}</p>
                        {/* Tags */}
                        {post.tags?.length > 0 && (
                          <div className="mt-2 flex flex-wrap gap-1">
                            {post.tags.map(tag => (
                              <span key={tag} className="px-2 py-1 bg-primary/10 text-primary rounded-md text-xs">#{tag}</span>
                            ))}
                          </div>
                        )}
                        {/* Actions */}
                        <div className="mt-3 grid grid-cols-4 text-sm text-muted-foreground">
                          <button className="group inline-flex items-center gap-2 px-2 py-1 rounded-md hover:bg-muted">
                            <MessageCircle className="h-4 w-4 group-hover:text-foreground" />
                            <span>{post.stats.comments}</span>
                          </button>
                          <button className="group inline-flex items-center gap-2 px-2 py-1 rounded-md hover:bg-muted">
                            <Repeat2 className="h-4 w-4 group-hover:text-foreground" />
                            <span>{post.stats.reposts}</span>
                          </button>
                          <button className="group inline-flex items-center gap-2 px-2 py-1 rounded-md hover:bg-muted">
                            <Heart className="h-4 w-4 group-hover:text-foreground" />
                            <span>{post.stats.likes}</span>
                          </button>
                          <button className="group inline-flex items-center gap-2 px-2 py-1 rounded-md hover:bg-muted">
                            <Share2 className="h-4 w-4 group-hover:text-foreground" />
                            <span>{post.stats.shares}</span>
                          </button>
                        </div>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              </motion.div>
            ))}
          </AnimatePresence>
        </div>

        {/* Footer note */}
        <div className="py-10 text-center text-xs text-muted-foreground">
          <p>Mock feed for demo. Connect real backend later.</p>
        </div>
      </div>
    </div>
  );
};

export default HomeFeed;
